import styled from "styled-components";
import forca0 from "../assets/forca0.png";
import forca1 from "../assets/forca1.png";
import forca2 from "../assets/forca2.png";
import forca3 from "../assets/forca3.png";
import forca4 from "../assets/forca4.png";
import forca5 from "../assets/forca5.png";
import forca6 from "../assets/forca6.png";

export default function Jogo({ countmiss, setCountMiss }) {
  const arraydaforca = [forca0, forca1, forca2, forca3, forca4, forca5, forca6];
  return (
    <Jogocontainer>
      <Main>
        <img src={arraydaforca[countmiss]} alt={forca0} />
      </Main>
      <Aside>
        <ChoseWordButton>Escolher palavra</ChoseWordButton>
        <Words>
          {/* <h1 className={wordcolor}>{wordgame}</h1> */}
          <h1>________</h1>
        </Words>
      </Aside>
    </Jogocontainer>
  );
}
const Jogocontainer = styled.div`
  display: flex;
`;
const ChoseWordButton = styled.button`
  margin-top: 10px;
  width: 200px;
  height: 50px;
  border-radius: 10px;
  color: white;
  background-color: green;
  font-weight: 700;
  &:hover {
    cursor: pointer;
  }
`;
const Main = styled.div`
  width: 60vw;
  display: flex;
  justify-content: center;
  img {
    height: 60vh;
  }
`;
const Aside = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: space-between;
  width: 40vw;
  height: 60vh;
`;
const Words = styled.div`
  display: flex;
  h1 {
    margin: 5px;
    font-size: 50px;
  }
`;
