export default function Jogo({ countmiss, setCountMiss }) {
  const arraydaforca = [forca0, forca1, forca2, forca3, forca4, forca5, forca6];
  return (
    <>
      <MainContainer>
        <img src={arraydaforca[countmiss]} alt={forca0} />
        <ChoseWordButton onClick={() => setCountMiss(countmiss + 1)}>
          botao
        </ChoseWordButton>
      </MainContainer>
    </>
  );
}
const ChoseWordButton = styled.button`
  width: 100px;
  height: 100px;
`;
const MainContainer = styled.div`
  width: 60vw;
  display: flex;
  justify-content: center;
  img {
    height: 60vh;
  }
`;
