import styled from "styled-components";
import letters from "../letters";
import Chute from "./Chute";
export default function Letras() {
  return (
    <>
      <Footer>
        <Letterbox>
          {letters.map((l) => (
            <button
            // onClick={() => Letterclicked(l)}
            // key={l}
            // disabled={letterused.includes(l)}
            >
              {l}
            </button>
          ))}
        </Letterbox>
        <Chute />
      </Footer>
    </>
  );
}
const Footer = styled.div`
  font-weight: 700;
  width: 100vw;
  height: 40vh;
  display: flex;
  align-items: center;
  justify-content: center;
  border-top: 1px solid brown;
  flex-direction: column;
  button {
    width: 50px;
    height: 50px;
    border-radius: 15px;
    text-transform: uppercase;
    &:hover {
      cursor: pointer;
      border: thick double #79818a;
    }
    &:disabled {
      background-color: #9faab5;
      border: 1px solid #9faab5;
      color: #79818a;
      cursor: default;
    }
  }
`;
const Letterbox = styled.div`
  max-width: 50%;
  min-width: 500px;
  height: auto;
  display: flex;
  flex-wrap: wrap;
  align-items: center;
  justify-content: center;
  margin-bottom: 10px;
`;
